import React, { useState } from 'react'

const CrearUsuario = () => {
    const [values ,setValues] = useState({
        nombre:'',
        ap:'',
    });

    const saveUsuarios = e =>{
        const formData = new FormData();
        const fileField = document.querySelector("input[type='file']");

        console.log(fileField.files);
        formData.append('avatar',fileField.files);

        e.preventDefault();
    }

    return(<div>
        <h4>Nuevo Usuario</h4>
        <form onSubmit={saveUsuarios}>
            <label>Nombre</label>
            <input value={values.nombre} onChange={e=>setValues({...values,nombre:e.target.value})} />
            <label>Apeido</label>
            <input value={values.ap} onChange={e=>setValues({...values,ap:e.target.value})}/>
            <input type='file' />
            <button type='submit' > Enviar</button>
        </form>
    </div>);
}

export default CrearUsuario;
